import {url} from './url.js'; 
import "./Stats.css";
import React,{useState, useEffect} from 'react';


import SideBar from "./SideBar";
function valider(event, idClient, budget, demande){
    event.preventDefault();
    alert("Je marche haha");
}

function Validation(){
    const[listeDemandes,setlisteDemandes] = useState([])
    useEffect(() =>{
        fetch(url+'listedemande')
        .then((response) =>response.json())
        .then((data) => setlisteDemandes(data))
    },[]);
    console.log(listeDemandes);
    return(
        <div className="Validation">
            <SideBar />
            <h1>Liste des rechargements de compte</h1>
            <div class = "tabG">
                <table>
                    <tr>
                        <th>Id</th>
                        <th>Nom Client</th>
                        <th>Budget</th>
                        <th>Demande</th>
                        <th>Action</th>
                    </tr>
                    {listeDemandes.map((demande) =>(
                        <tr>
                        <td>{demande.idClient}</td>
                        <td>{demande.nom}</td>
                        <td>{demande.budget}</td>
                        <td>{demande.demande}</td>
                        <td><button onClick={valider(event , demande.idClient , demande.budget , demande.demande)}>Valider</button></td>
                    </tr>
                    )

                    )} 

                    <tr>
                    </tr>
                </table>
            </div>
        </div>
    );
}

export default Validation
